    const sensorData = { ...modifiedPayload[0] };  // Clone the sensor metrics object

    // Iterate over each metric in the sensor data object
    Object.keys(sensorData).forEach(metric => {
        // Skip non-numeric values (such as a device ID if present)
        if (typeof sensorData[metric] !== "number") return;

        const value = sensorData[metric];
        const randomValue = Math.random();

        if (randomValue < 0.3) {
            // 30% chance of a hike
            const hike = (1 + Math.random() * (anomalyPercentage / 100));
            sensorData[metric] = parseFloat((value * hike).toFixed(2));
        } else if (randomValue < 0.6) {
            // 30% chance of a drop
            const drop = (1 - Math.random() * (anomalyPercentage / 100));
            sensorData[metric] = parseFloat((value * drop).toFixed(2));
        }
        // Otherwise, no change
    });

    // Replace the original sensor data object with the modified one in the array
    modifiedPayload[0] = sensorData;
}

// Set the modified payload array back to msg.payload
