let value = msg.payload;
let base64Payload = '';

if (msg.topic == 'interval')
{
    // Validate the input
    if (isNaN(value) || value < 0 || value > 16777215) { // 3 bytes max value
     node.error("Invalid interval value. Must be between 0 and 16777215 seconds.");
     return null;
    }

    // Command Code (0x01) followed by 3-byte interval
    let commandCode = 0x01;
    let intervalHex = Buffer.alloc(3);
    intervalHex.writeUIntBE(value, 0, 3); // Write the interval as 3 bytes

    // Combine Command Code and Interval
    let payload = Buffer.concat([Buffer.from([commandCode]), intervalHex]);

    // Convert to Base64
    base64Payload = payload.toString('base64');

}

if (msg.topic == 'adr') {
   if (value !== false && value !== true) {
    // Return an error if the data rate is invalid.
    node.error("Invalid data rate. Must be False or True.");
    return null;
}

    // Base command template (type code and fixed bytes).
    let baseCommand = [0x22, 0x00, 0xaa, 0xff];

    // Set the third byte to the desired data rate.
    baseCommand[2] = (value === true) ? 1 : 0; 

    // Convert the array to a Buffer.
    let buffer = Buffer.from(baseCommand);

    // Convert to Base64
    base64Payload = buffer.toString('base64');
}


